INSERT INTO news_stories (id, record_id, url, body_text, ai_summary) VALUES ('40e77d65-d99c-43a4-8264-adad4e4eb4f6', '258801a7-2821-4978-adc9-9a3ac78ede15', 'https://www.thestar.com/news/gta/2015/05/23/brampton-centennial-students-still-haunted-by-shooting-rampage-40-years-on.html', 'ADVERTISEMENTSkip to Main ContentSign Inperson_outlineSkip to Main ContentSign IncloseSign Insearchcanceledit_locationThe Star EditionCHANGE LOCATIONexpand_moreManage Profilekeyboard_arrow_rightSubscriptionskeyboard_arrow_rightBilling Informationkeyboard_arrow_rightNewsletterskeyboard_arrow_rightNotificationskeyboard_arrow_rightSign Out This copy is for your personal non-commercial use only. To order
