INSERT INTO news_stories (id, record_id, url, body_text, ai_summary) VALUES ('2d5f2fc9-cd57-45fa-b49f-8c7ba3935346', 'd8e545a2-3de5-4570-a72f-337be55df659', 'https://www.thestar.com/news/gta/2022/10/11/accused-serial-killer-stands-trial-in-first-two-of-four-toronto-shooting-murders.html', 'ADVERTISEMENTSkip to Main ContentSign Inperson_outlineSkip to Main ContentSign IncloseSign Insearchcanceledit_locationThe Star EditionCHANGE LOCATIONexpand_moreManage Profilekeyboard_arrow_rightSubscriptionskeyboard_arrow_rightBilling Informationkeyboard_arrow_rightNewsletterskeyboard_arrow_rightNotificationskeyboard_arrow_rightSign Out This copy is for your personal non-commercial use only. To order
