INSERT INTO news_stories (id, record_id, url, body_text, ai_summary) VALUES ('46e4d5a7-50d2-484e-a0e6-73e89b7a9b60', 'd8e545a2-3de5-4570-a72f-337be55df659', 'https://www.thestar.com/news/gta/2019/10/01/two-men-in-critical-condition-after-shooting-near-jane-and-finch.html', 'ADVERTISEMENTSkip to Main ContentSign Inperson_outlineSkip to Main ContentSign IncloseSign Insearchcanceledit_locationThe Star EditionCHANGE LOCATIONexpand_moreManage Profilekeyboard_arrow_rightSubscriptionskeyboard_arrow_rightBilling Informationkeyboard_arrow_rightNewsletterskeyboard_arrow_rightNotificationskeyboard_arrow_rightSign Out This copy is for your personal non-commercial use only. To order
