INSERT INTO news_stories (id, record_id, url, body_text, ai_summary) VALUES ('5633f073-67d2-4ccf-85ba-f2077883ed2c', '472c2ece-8a78-4d34-bc1b-e8972836e196', 'https://www.blogto.com/city/2022/04/toronto-police-arrest-accused-2-shootings/', 'Home
