INSERT INTO news_stories (id, record_id, url, body_text, ai_summary) VALUES ('041945cc-ddf6-4301-96dc-184cedb13197', 'dbed0305-3a19-4eb8-a3ee-1d9537c78fb0', 'https://www.thestar.com/news/gta/2022/09/13/gunman-behind-gta-rampage-had-criminal-record-dating-back-at-least-20-years.html', 'ADVERTISEMENTSkip to Main ContentSign Inperson_outlineSkip to Main ContentSign IncloseSign Insearchcanceledit_locationThe Star EditionCHANGE LOCATIONexpand_moreManage Profilekeyboard_arrow_rightSubscriptionskeyboard_arrow_rightBilling Informationkeyboard_arrow_rightNewsletterskeyboard_arrow_rightNotificationskeyboard_arrow_rightSign Out This copy is for your personal non-commercial use only. To order
