INSERT INTO news_stories (id, record_id, url, body_text, ai_summary) VALUES ('b1881c76-bb94-4fe5-b3d5-bf8b2db6a5ff', 'eccc2a27-4a1b-415d-b6fc-b85a6b62efc2', 'https://www.thestar.com/news/canada/2023/03/17/edmonton-police-expected-to-provide-new-details-in-fatal-shooting-of-two-officers.html', 'ADVERTISEMENTSkip to Main ContentSign Inperson_outlineSkip to Main ContentSign IncloseSign Insearchcanceledit_locationThe Star EditionCHANGE LOCATIONexpand_moreManage Profilekeyboard_arrow_rightSubscriptionskeyboard_arrow_rightBilling Informationkeyboard_arrow_rightNewsletterskeyboard_arrow_rightNotificationskeyboard_arrow_rightSign Out This copy is for your personal non-commercial use only. To order
